UPDATE invoice_line set call_identifier = subquery.phone_number FROM
(SELECT case when phone_number IS NOT NULL then phone_number when chat_identifier IS NOT NULL then chat_identifier else active_response_identifier end as phone_number, id FROM invoice_line where (phone_number IS NOT NULL or active_response_identifier IS NOT NULL or chat_identifier IS NOT NULL))
AS subquery WHERE invoice_line.id = subquery.id;


