CREATE TABLE backup_meta_field_value as
SELECT * FROM meta_field_value WHERE meta_field_name_id in 
(SELECT id FROM meta_field_name 
WHERE name in ('INBOUND_CALLS_IDENTIFIER','CHAT_IDENTIFIER','ACTIVE_RESPONSE_IDENTIFIER'));

CREATE TABLE backup_order_line_meta_field_map as
SELECT * FROM order_line_meta_field_map 
WHERE meta_field_value_id in (SELECT id FROM backup_meta_field_value);

CREATE TABLE backup_order_change_meta_field_map as
SELECT * FROM order_change_meta_field_map 
WHERE meta_field_value_id in (SELECT id FROM backup_meta_field_value);

CREATE TABLE backup_order_change_plan_item_meta_field_map as
SELECT * FROM order_change_plan_item_meta_field_map 
WHERE meta_field_value_id in (SELECT id FROM backup_meta_field_value);

CREATE TABLE backup_meta_field_name as 
SELECT * FROM meta_field_name 
WHERE name in ('INBOUND_CALLS_IDENTIFIER','CHAT_IDENTIFIER','ACTIVE_RESPONSE_IDENTIFIER');


DELETE FROM order_line_meta_field_map 
