changeSet.sql:
ALTER TABLE payment
ALTER payment_date
TYPE timestamp without time zone;

UPDATE payment
   SET payment_date = payment_date AT TIME ZONE 'UTC' AT TIME ZONE 'Australia/Sydney'
 WHERE TO_CHAR(payment_date, 'yyyy-MM-dd HH:MI:ss') NOT LIKE '%12:00:00%';

